image.onload = function() {
    // 2.\u5F00\u59CB\u7528Canvas\u6765\u7ED8\u5236\u56FE\u7247
    ctx.drawImage(image, 0, 0, 180, 130)

    // 3.\u7ED8\u5236\u6298\u7EBF
    ctx.beginPath()
    ctx.moveTo(40, 100)
    ctx.lineTo(50, 70)
    ctx.lineTo(60, 90)
    ctx.lineTo(100, 30)
    ctx.lineTo(170, 90)
    ctx.stroke()
}
